import Memory from './memory';
class PC {
    constructor() {
        this.addr = 0;
    }
    setPC(addr) {
        this.addr = addr;
    }
    getPC() {
        return this.addr;
    }
    offsetPC(bytes) {
        this.addr += bytes;
    }
    readByte() {
        const byte = Memory.getByte(this.addr);
        this.addr += 1;
        return byte;
    }
    readWord() {
        const word = Memory.getWord(this.addr);
        this.addr += 2;
        return word;
    }
    // read the next byte as four two bit crumbs
    readCrumbs() {
        const byte = Memory.getByte(this.addr);
        this.addr += 1;
        /* eslint-disable no-bitwise */
        return [
            (byte & 192) >> 6,
            (byte & 48) >> 4,
            (byte & 12) >> 2,
            byte & 3,
        ];
        /* eslint-enable no-bitwise */
    }
}
const singleton = new PC();
export default singleton;
